pragma solidity ^0.5.3;

contract Dice{

	struct Bet{ //struktura Oklade
		bool  currentBet; //trenutačna oklada
		uint256 rollResult;
		bool betWon;
	}

	mapping(address => Bet) private bets; // praćenje oklada igrača

	uint256 private randomFactor; // randomiziranje rezultata

	event GameResult(address bidder, bool currentBet , uint256 rollResult); // event koji služi za praćenje stanja rezultata

	constructor() public{ // konstruktor sa randomizacijom rezultata postavljenom na neki broj
		randomFactor = 10;
	}

	function betAndRoll  (bool isBetEven) public {
		bets[msg.sender].currentBet = isBetEven;
		bets[msg.sender].rollResult = random();
		randomFactor += bets[msg.sender].rollResult;

		// Check if the result is even
		bool isResultEven = false;
		isResultEven = bets[msg.sender].rollResult % 2 == 0;

		// Check if we won
		bets[msg.sender].betWon = isResultEven == isBetEven;
		if (bets[msg.sender].betWon){
			msg.sender.transfer(100000000000000);
		}
		emit GameResult(msg.sender, bets[msg.sender].betWon, bets[msg.sender].rollResult);
	}

    function random() private view returns (uint256) { // funkcija za dobivanje nasumičnog broja
       	uint256 blockValue = uint256(blockhash(block.number-1 + block.timestamp));
        blockValue = blockValue + uint256(randomFactor);
        uint256 result = (blockValue % 5) + 1; // ovdje trebamo dobivati brojeve od 2 do 12 HINT: bolckValue % 11 + 2
		//randomFactor += result;
		return result;
    }

    function() external payable{} // fallback funkcija koja se poziva ukoliko je contract pozvan, ali nije pozvana ni jedna druga funkcija ili nisu poslani nikakvi podaci


}
